#home-header {
  font-family: "Copernicus", sans-serif;
  font-weight: 300 !important;
  font-size: 50px;
  line-height: 1.2;
  margin-bottom: 1rem;
  color: --text-000;
  display: flex;
  align-items: baseline;
  justify-content: center;
  flex-wrap: nowrap;
}

/* Responsive layout for the home header - left align on larger screens */
@media (min-width: 768px) {
  #home-header {
    justify-content: flex-start;
  }
}

.build-with {
  font-family: "Copernicus", sans-serif;
  white-space: nowrap;
  letter-spacing: -0.02em;
}

.claude-wordmark-wrapper {
  display: inline-flex;
  align-items: baseline;
  margin-left: 10px; /* Space between "Build with" and the wordmark */
}

.claude-wordmark {
  height: 40px; /* Adjust this value to match your desired size */
  width: auto;
  position: relative;
}

.dark #home-header {
  color: white;
}

.description-text {
  color: black;
}

.dark .description-text {
  color: white;
}

.dark .claude-wordmark {
  filter: invert(1);
}

:root {
  --bg-color: #f0efea;
}

.dark {
  --bg-color: #2b2b2b;
}

#background-div {
}

body,
input,
#category-select,
.dropdown-item,
#table-of-contents {
  font-family: "Styrene", sans-serif;
}

.eyebrow {
  font-family: "Styrene Display", sans-serif;
  text-transform: uppercase;
  letter-spacing: 0.02rem;
}

#content-container {
  font-family: "Tiempos", serif;
}

#content-container h1,
#content-container h2,
#content-container h3,
#content-container h4,
#content-container h5,
#content-container h6 {
  font-family: "Styrene Display", sans-serif;
}

#content-container p {
  font-size: 1rem;
  line-height: 1.65rem;
}

.font-extrabold {
  font-weight: 600 !important;
}

.wide-table {
  width: 100%;
  overflow-x: auto;
}

.wide-table table {
  width: 175%;
  margin-bottom: 0;
}

/* Prompt Library */
#prompt-library-container {
  margin: 4rem auto;
  max-width: 48rem;
  padding-left: 1.25rem;
  padding-right: 1.25rem;
}

.prompt-library-title {
  font-size: 24px;
  text-align: center;
  font-weight: 700;
  color: #1f2937;
}

.dark .prompt-library-title {
  color: #e5e7eb;
}

.prompt-library-description {
  margin-top: 1rem;
  text-align: center;
}

.main-content {
  margin-bottom: 10rem;
  max-width: 64rem;
  margin-left: auto;
  margin-right: auto;
  padding-left: 1.25rem;
  padding-right: 1.25rem;
}

.prompt-controllers {
  display: flex;
  gap: 0.5rem;
}

.prompt-search-container {
  position: relative;
  flex: 1 1 0%;
}

.prompt-search-icon-container {
  display: flex;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  align-items: center;
  padding-left: 0.75rem;
}

.prompt-search-icon {
  margin-left: 0.25rem;
  margin-right: 0.75rem;
  flex: none;
  width: 1rem;
  height: 1rem;
  background-color: #6b7280;
  mask-image: url(https://mintlify.b-cdn.net/v6.5.1/solid/magnifying-glass.svg);
  mask-repeat: no-repeat;
  mask-position: center center;
}

input.prompt-search-bar {
  display: block;
  height: 2.5rem;
  padding-left: 2.5rem;
  border-radius: 0.75rem;
  border-width: 1px;
  background-color: #ffffff;
  width: 100%;
  color: #111827;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
}

.dark input.prompt-search-bar {
  color: #ffffff;
  background-color: rgb(var(--background-dark));
  border-color: #d1d5db1a;
}

input.prompt-search-bar:focus {
  outline-color: rgb(var(--primary));
}

.dark input.prompt-search-bar:focus {
  outline-color: rgb(var(--primary-light));
}

.dark .prompt-search-icon {
  background-color: #ffffff80;
}

#category-select {
  padding-left: 1rem;
  padding-right: 2.5rem;
  height: 2.5rem;
  display: flex;
  align-items: center;
  border-radius: 0.75rem;
  border-width: 1px;
  color: #111827;
  background-color: #ffffff;
  cursor: pointer;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  white-space: nowrap;
}

.dark #category-select {
  background-color: rgb(var(--background-dark));
  border-color: #d1d5db1a;
  color: #ffffff;
}

#category-select:hover {
  background-color: #f9fafb;
}

.dark #category-select:hover {
  background-color: #ffffff0d;
}

#category-select:focus {
  outline-color: rgb(var(--primary));
}

.dark #category-select:focus {
  outline-color: rgb(var(--primary-light));
}

#categories-dropdown {
  top: calc(100% + 4px);
  padding: 0.5rem 0.5rem;
  display: none;
  position: absolute;
  z-index: 10;
  border-radius: 0.75rem;
  border-width: 1px;
  width: 100%;
  color: #111827;
  background-color: #ffffff;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
}

.dark #categories-dropdown {
  background-color: rgb(var(--background-dark));
  border-color: #d1d5db1a;
  color: #ffffff;
}

#categories-dropdown-clickout {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 0;
}

.dropdown-icon-container {
  display: flex;
  position: absolute;
  top: 0;
  bottom: 0;
  right: 0;
  align-items: center;
  padding-right: 0.25rem;
}

.dropdown-icon {
  margin-left: 0.25rem;
  margin-right: 0.75rem;
  flex: none;
  width: 0.75rem;
  height: 0.75rem;
  background-color: #6b7280;
  mask-image: url(https://mintlify.b-cdn.net/v6.5.1/solid/caret-down.svg);
  mask-repeat: no-repeat;
  mask-position: center center;
}

.dark .dropdown-icon {
  background-color: #ffffff80;
}

#prompts-container {
  grid-template-columns: repeat(1, minmax(0, 1fr));
  gap: 2rem;
}

.dropdown-item {
  padding: 0.25rem 0.5rem;
  border-radius: 0.375rem;
  display: flex;
  align-items: center;
  cursor: pointer;
}

.dropdown-item:hover {
  background-color: #f9fafb;
}

.dark .dropdown-item:hover {
  background-color: #ffffff0d;
}

.check-icon {
  mask-image: url(https://mintlify.b-cdn.net/v6.5.1/solid/check.svg);
  height: 0.875rem;
  width: 1rem;
  background-color: rgb(var(--primary-light));
  mask-repeat: no-repeat;
  mask-position: center center;
}

.prompt-card {
  margin: -0.75rem;
  padding: 0.75rem;
  display: flex;
  border-radius: 1rem;
}

.prompt-card:hover {
  background-color: #03071208;
}

.dark .prompt-card:hover {
  background-color: #ffffff08;
}

.prompt-icon-container {
  display: flex;
  flex: none;
  align-items: center;
  justify-content: center;
  margin-right: 1.5rem;
  border-radius: 0.75rem;
  height: 4rem;
  width: 4rem;
  background-color: #cb785c1a;
}

.prompt-icon {
  height: 1.5rem;
  width: 1.5rem;
  background-color: rgb(var(--primary-light));
  mask-repeat: no-repeat;
  mask-position: center center;
}

.prompt-title {
  color: rgb(31 41 55);
  font-weight: 600;
}

.dark .prompt-title {
  color: rgb(229 231 235);
}

.prompt-description {
  margin-top: 0.25rem;
}

#prompts-container {
  display: grid;
  margin-top: 2.5rem;
}

@media (min-width: 640px) {
  #category-select {
    width: 16rem;
  }
}

@media (min-width: 1024px) {
  #prompts-container {
    grid-template-columns: repeat(2, minmax(0, 1fr));
  }
}

/* Home page card styling */
.home-cards-custom {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(2, auto);
  gap: 1.5rem;
}

.home-cards-custom .card {
  background: transparent;
  border: 0.5px solid hsl(var(--border-300));
  border-radius: 12px;
  padding: 0.25rem;
}

/* Responsive: change to 2 columns on tablet, single column on mobile */
@media (max-width: 1024px) {
  .home-cards-custom {
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(3, auto);
  }
}

@media (max-width: 768px) {
  .home-cards-custom {
    grid-template-columns: 1fr;
    grid-template-rows: repeat(6, auto);
  }
}

/* Utility classes */
.relative {
  position: relative;
}

.flex-1 {
  flex: 1 1 0%;
}

/* These styles mirror our design system (converted to plain CSS with Claude's help) from https://ui.product.ant.dev/button */

/* Base button styles */
.btn {
  position: relative;
  display: inline-flex;
  gap: 0.5rem;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  min-width: 5rem;
  height: 2.25rem;
  padding: 0.5rem 1rem;
  white-space: nowrap;
  font-family: Styrene;
  font-weight: 600;
  border-radius: 0.5rem;
  &:active {
    transform: scale(0.985);
  }

  /* Size variants */
  &.size-xs {
    height: 1.75rem;
    min-width: 3.5rem;
    padding: 0 0.5rem;
    border-radius: 0.25rem;
    font-size: 0.75rem;
    gap: 0.25rem;
  }

  &.size-sm {
    height: 2rem;
    min-width: 4rem;
    padding: 0 0.75rem;
    border-radius: 0.375rem;
    font-size: 0.75rem;
  }

  &.size-lg {
    height: 2.75rem;
    min-width: 6rem;
    padding: 0 1.25rem;
    border-radius: 0.6rem;
  }

  &:disabled {
    pointer-events: none;
    opacity: 0.5;
    box-shadow: none;
  }

  &:focus-visible {
    outline: none;
    --tw-ring-offset-shadow: var(--tw-ring-inset) 0 0 0 var(--tw-ring-offset-width) var(--tw-ring-offset-color);
    --tw-ring-shadow: var(--tw-ring-inset) 0 0 0 calc(1px + var(--tw-ring-offset-width)) var(--tw-ring-color);
    box-shadow: var(--tw-ring-offset-shadow), var(--tw-ring-shadow);
  }

  /* Primary variant */
  &.primary {
    font-weight: 600;
    color: hsl(var(--oncolor-100));
    background-color: hsl(var(--accent-main-100));
    background-image: linear-gradient(
      to right,
      hsl(var(--accent-main-100)) 0%,
      hsl(var(--accent-main-200) / 0.5) 50%,
      hsl(var(--accent-main-200)) 100%
    );
    background-size: 200% 100%;
    background-position: 0% 0%;
    border: 0.5px solid hsl(var(--border-300) / 0.25);
    box-shadow: 
      inset 0 0.5px 0px rgba(255, 255, 0, 0.15),
      0 1px 1px rgba(0, 0, 0, 0.05);
    text-shadow: 0 1px 2px rgb(0 0 0 / 10%);
    transition: all 150ms cubic-bezier(0.4, 0, 0.2, 1);

    &:hover {
      background-position: 100% 0%;
      background-image: linear-gradient(
        to right,
        hsl(var(--accent-main-200)) 0%,
        hsl(var(--accent-main-200)) 100%
      );
    }

    &:active {
      background-color: hsl(var(--accent-main-000));
      box-shadow: inset 0 1px 6px rgba(0, 0, 0, 0.2);
      transform: scale(0.985);
    }
  }

  /* Flat variant */
  &.flat {
    font-weight: 500;
    color: hsl(var(--oncolor-100));
    background-color: hsl(var(--accent-main-100));
    transition: background-color 150ms;

    &:hover {
      background-color: hsl(var(--accent-main-200));
    }
  }

  /* Secondary variant */
  &.secondary {
    font-weight: 600;
    color: hsl(var(--text-100) / 0.9);
    background-image: radial-gradient(
      ellipse at center,
      hsl(var(--bg-500) / 0.1) 50%,
      hsl(var(--bg-500) / 0.3) 100%
    );
    border: 0.5px solid hsl(var(--border-400));
    transition: color 150ms, background-color 150ms;

    &:hover {
      color: hsl(var(--text-000));
      background-color: hsl(var(--bg-500) / 0.6);
    }

    &:active {
      background-color: hsl(var(--bg-500) / 0.5);
    }
  }

  /* Outline variant */
  &.outline {
    font-weight: 600;
    color: hsl(var(--text-200));
    background-color: transparent;
    border: 1.5px solid currentColor;
    transition: color 150ms, background-color 150ms;

    &:hover {
      color: hsl(var(--text-100));
      background-color: hsl(var(--bg-400));
      border-color: hsl(var(--bg-400));
    }
  }

  /* Ghost variant */
  &.ghost {
    color: hsl(var(--text-200));
    border-color: transparent;
    transition: color 150ms, background-color 150ms;

    &:hover {
      color: hsl(var(--text-100));
      background-color: hsl(var(--bg-500) / 0.4);
    }

    &:active {
      background-color: hsl(var(--bg-400));
    }
  }

  /* Underline variant */
  &.underline {
    opacity: 0.8;
    text-decoration-line: none;
    text-underline-offset: 3px;
    transition: all 150ms;

    &:hover {
      opacity: 1;
      text-decoration-line: underline;
    }

    &:active {
      transform: scale(0.985);
    }
  }

  /* Danger variant */
  &.danger {
    font-weight: 600;
    color: hsl(var(--oncolor-100));
    background-color: hsl(var(--danger-100));
    transition: background-color 150ms;

    &:hover {
      background-color: hsl(var(--danger-200));
    }
  }
}
