  padding: 1rem;
  border-radius: 4px;
  overflow: auto;
}

.code-snippet code {
  font-family: monospace;
}

.card {
  background-color: #f8f9fa;
  border-radius: 8px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
  padding: 1.5rem;
  margin-bottom: 1.5rem;
  position: relative;
  cursor: pointer;
  display: flex;
  align-items: center;
}

.color-scheme--dark .card {
  background-color: var(--devsite-ref-palette--grey800);
}

.card > img {
  width: 40%;
  height: auto;
  margin-right: 1rem;
  object-fit: cover;
}
.card > h3, .card > p{
  margin-left: 0;

}

.card-text-content {
  display: flex;
  flex-direction: column;

}

.gemini-api-card-overview {
  background: var(--devsite-background-1);
  border: 1px solid var(--googledevai-border-color);
  border-radius: 9px;
  box-shadow: var(--gemini-api-elevation-1dp);
  height: 14rem;
  padding: 1rem;
  transition: box-shadow 0.3s ease-in-out;
  width: 100%;
}

.gemini-api-card-overview:hover {
  box-shadow: var(--gemini-api-elevation-3dp);
}

.gemini-api-card-overview a:empty {
  display: block;
  position: relative;
  height: 14rem;
  width: 115%;
  top: -14.5rem;
  left: -1rem;
}

.gemini-api-card-overview a:empty:focus {
  border: 2px solid var(--devsite-primary-color);
  border-radius: 9px;
}

@media only screen and (min-width: 625px) {
  .gemini-api-recommended {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-column-gap: 3rem;
  }
}

.card a {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  text-decoration: none;
  color: inherit;

}

.card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
}

.cta-button {
  background-color: #4285f4;
  color: #fff;
  padding: 1rem 2rem;
  border-radius: 4px;
  text-decoration: none;
  display: inline-block;
  transition: background-color 0.2s;
}

.cta-button:hover {
  background-color: #357ae8;
}


.capabilities-list li {
  list-style-type: disc;
  margin-left: 20px;
  margin-bottom: 0.5rem;

}

.models-section {
  display: flex;
  justify-content: space-between;
  align-items: end;
}

.gemini-api-model-button {
  background-color: #4285f4;
  color: white;
  padding: 0.8rem 1.5rem;
  border-radius: 4px;
  text-decoration: none;
  font-size: 1rem;
}

.card-image {
  width: 50px;
  height: 50px;
  margin-right: 1rem;
  object-fit: cover;
}

.card-overview-bottom {
  padding: 2rem;
  border-radius: 8px;
  text-align: center;
  background-image: linear-gradient(to right, #217BFE, #078EFB);
  color: white;
}

@media only screen and (max-width: 625px) {
  .gemini-api-card-overview {
    margin-bottom: 1.5rem;
    height: 10rem;
  }
  .gemini-api-card-overview a:empty {
    height: 10rem;
    top: -12.1rem;
  }
  .gemini-api-card-title {
    margin: 0rem;
  }
  .card {
    display: block;
  }
  .card > img {
    width: 100%;
  }
  .models-section {
    display: block;
  }
  .ais {
    width: 100%;
  }
}

@media (max-width: 768px) {
  #meet-the-models {
    margin: 0rem;
  }
}




















